import React, { useEffect, useState } from "react";
import {
  collection,
  query,
  onSnapshot,
  getDocs,
  deleteDoc,
  updateDoc,
  doc,
} from "firebase/firestore";
import { dbService } from "fbase";
import styled from "styled-components";
import { async } from "@firebase/util";
import { ref, deleteObject } from "firebase/storage";

//style 적용
// const StyledUl = styled.ul`
//   display: inline-block;
//   list-style: none;
//   background-color: skyblue;
//   width: 800px;
//   border: 1px solid gray;
//   text-align: center;
//   border-radius: 25px;
//   margin-right: 20px;
// `;
// const StyledLi = styled.li`
//   display: inline-block;
//   height: 50px;
//   line-height: 50px;
//   font-weight: bold;
//   padding: 0px;
// `;
// const StyledTime = styled.li`
//   text-align: right;
// `;
const Img = styled.img`
  height: 200px;
  border: none;
`;

//firestore data 호출
const ReadGongsa = ({ userObj }) => {
  const [gongsaList, setGongsaList] = useState([]);

  useEffect(() => {
    getGongsaData();
    console.log(userObj);
  }, []);

  const getGongsaData = async () => {
    const querySnapshot = await getDocs(collection(dbService, "gongsa"));
    const querySnapshotArray = [];

    querySnapshot.forEach((doc) => {
      querySnapshotArray.push(doc.data());
    });

    setGongsaList(querySnapshotArray);
  };

  //수정, 삭제 작업
  const [editing, setEditing] = useState(false);
  const [newOweet, setNewOweet] = useState(userObj.text);
  const gongsaTextRef = doc(dbService, "gongsa", `${userObj.id}`);
  const onDeleteClick = async () => {
    const ok = window.confirm("Are you sure want to delete this text?");
    if (ok) {
      await deleteDoc(gongsaTextRef);
      // await deleteObject(ref(storageService, oweetObj.attachmentURL));
    }
  };
  const toggleEditing = () => setEditing((prev) => !prev);
  const onSubmit = async (event) => {
    event.preventDefault();
    console.log(userObj, newOweet);
    await updateDoc(gongsaTextRef, { text: newOweet });
    setEditing(false);
  };
  const onChange = (event) => {
    const {
      target: { value },
    } = event;
    setNewOweet(value);
  };

  //firestore data 표시
  return (
    <div>
      <h2>현성씨 Component</h2>
      {gongsaList.map((el, index) => (
        <div key={index}>
          <ul>
            <li>{el.addr}</li>
            <li>{el.createdAt}</li>
          </ul>
          <li>
            <Img src={el.attachmentUrl} />
          </li>
          <li>
            {editing ? (
              <>
                <form onSubmit={onSubmit}>
                  <input
                    type="text"
                    placeholder="Edit your Oweet"
                    onChange={onChange}
                    value={newOweet}
                    required
                  />
                  <input type="submit" value="Update Oweet" />
                </form>
                <button onClick={toggleEditing}>Cancel</button>
              </>
            ) : (
              <>
                <h4>{userObj.text}</h4>
                {userObj.attachmentURL && (
                  <img
                    src={userObj.attachmentURL}
                    width="100px"
                    height="100px"
                  />
                )}
                {isOwner && (
                  <>
                    <button onClick={onDeleteClick}>Delete Oweet</button>
                    <button onClick={toggleEditing}>Edit Oweet</button>
                  </>
                )}
              </>
            )}
          </li>
        </div>
      ))}
    </div>
  );
};

export default ReadGongsa;
