import React, { useEffect, useRef, useState } from "react";
import { dbService, storageService } from "../fbase";
import { collection, addDoc } from "firebase/firestore";
import { v4 as uuidv4 } from "uuid";
import EXIF from "exif-js";
import { ref, uploadString, getDownloadURL } from "firebase/storage";

const Register = ({ userObj }) => {
  const [gongsa, setGongsa] = useState("");
  const [gongsas, setGongsas] = useState([]);
  const [attachment, setAttachment] = useState("");
  const [date, setDate] = useState("");
  const [GPSla, setGPSLa] = useState([]);
  const [GPSlong, setGPSLong] = useState([]);

  // const [meta, setMeta] = useState(""); //metadata
  const meta = useRef(null);

  const onSubmit = async (event) => {
    event.preventDefault();
    let attachmentUrl = "";

    if (attachment !== "") {
      const attachmentRef = ref(storageService, `${userObj.uid}/${uuidv4()}`);
      const response = await uploadString(
        attachmentRef,
        attachment,
        "data_url"
      );
      attachmentUrl = await getDownloadURL(response.ref);
    }

    const gongsaObj = {
      text: gongsa,
      createdAt: meta.exifdata.DateTime,
      createdId: userObj.uid,
      attachmentUrl,
      // phoneNumber,
    };
    await addDoc(collection(dbService, "gongsa"), gongsaObj);
    setGongsa("");
    setAttachment("");
  };

  const onChange = (event) => {
    const {
      target: { value },
    } = event;
    setGongsa(value);
  };

  const onFileChange = (event) => {
    const {
      target: { files },
    } = event;
    const theFile = files[0];
    const reader = new FileReader();
    reader.onloadend = (finishedEvent) => {
      const {
        currentTarget: { result },
      } = finishedEvent;
      setAttachment(result);
    };
    reader.readAsDataURL(theFile);
    //메타데이터 console
    if (theFile && theFile.name) {
      EXIF.getData(theFile, function () {
        let exifData = EXIF.pretty(this);
        let gpsLa = EXIF.getTag(this, "GPSLatitude");
        let gpsLong = EXIF.getTag(this, "GPSLongitude");
        let la = [];
        let long = [];

        if (exifData) {
          console.log(exifData);
          console.log(gpsLa);
          console.log(gpsLong);
          for (let i = 0; i < gpsLa.length; i++) {
            la[i] = gpsLa[i].numerator / gpsLa[i].denominator;
          }
          for (let i = 0; i < gpsLong.length; i++) {
            long[i] = gpsLong[i].numerator / gpsLong[i].denominator;
          }
          console.log(la, long);

          setDate(EXIF.getTag(this, "DateTime"));
          setGPSLa(
            parseInt(la[0]) + (60 * parseInt(la[1]) + parseFloat(la[2])) / 3600
          );
          setGPSLong(long[0] + (60 * long[1] + parseFloat(long[2])) / 3600);
          // meta.current = theFile;
          // const metaArray = [];

          // // for (const element of meta.current.exifdata.GPSLongitude) {
          // //   metaArray.push(parseFloat(Object.values(element)[0]));
          // //   console.log(metaArray);
        } else {
          console.log("No EXIF data found in image '" + theFile.name + "'.");
        }
      });
    }
  };

  const onClearAttachment = () => {
    setAttachment(null);
    fileInput.current.value = null;
  };

  const fileInput = useRef();

  return (
    <div>
      <h2>민정씨 Component</h2>
      <form onSubmit={onSubmit}>
        <input
          value={gongsa}
          onChange={onChange}
          type="text"
          placeholder="공사 정보 입력"
        />
        <input
          type="file"
          accept="image/*"
          onChange={onFileChange}
          ref={fileInput}
        />
        <input type="submit" value="submit" />
        {attachment && (
          <div>
            <img src={attachment} width="50px" height="50px" />
            <button onClick={onClearAttachment}>Clear</button>
          </div>
        )}
        {console.log(meta)}
        time : {date}
        <hr />
        GPSLatitude : {GPSla}
        <hr />
        GPSLongitude : {GPSlong}
      </form>
    </div>
  );
};

//ref 조건 별 위경도 계산

export default Register;
