      <h2>민정씨 Component</h2>
      <form onSubmit={onSubmit}>
        <input
          value={gongsa}
          onChange={onChange}
          type="text"
          placeholder="공사 정보 입력"
        />
        <input type="file" accept="image/*" onChange={onFileChange}/>  
        <input type="submit" value="submit" />
        {/* {attachment && (
