      text: gongsa,
      createdAt: Date.now(),
      createdId: userObj.uid,
      attachmentUrl,
      // phoneNumber,
    };
    await addDoc(collection(dbService, "gongsa"), gongsaObj);
    setGongsa("");
    setAttachment("");
  };

